<script lang="ts">
	import { page } from '$app/stores';

	let title = '';

	page.subscribe((page) => {
		if (page.route.id === '/') {
			return (title = 'Home');
		}

		if (page.route.id === '/nodes/[address]') {
			return (title = 'Stats');
		}

		if (page.route.id === '/api') {
			return (title = 'Public API');
		} else {
			if (page.route.id) {
				return (title = page.route.id?.substring(1));
			}
		}
	});
</script>

<div
	class="flex w-full max-w-screen-lg flex-col items-start bg-neutral-200 px-8 py-8 dark:bg-neutral-800"
>
	<h1
		class="z-20 rounded-md bg-slate-100 bg-opacity-80 p-4 text-5xl capitalize tracking-tighter text-slate-700 dark:bg-neutral-900 dark:text-white"
	>
		{title}
	</h1>
</div>

